const a=document.querySelector("#com");const b=document.querySelectorAll('.title');const c=[];b.forEach(t=>{c.push(t.innerText)});let d=" ";let e=new Date();setInterval(()=>{e=new Date();if (e.getSeconds()%10==0){let f=c[0];c.shift();c.push(f);b.forEach((t,i)=>{t.innerText=c[i]});}d=e.getSeconds()%2==0?" ":'<span class="flash" style="color:var(--txt-accent-color)">:</span>';a.innerHTML=e.getHours().toLocaleString("fr-FR",{minimumIntegerDigits:2,useGrouping: false,})+d+e.getMinutes().toLocaleString("fr-FR",{minimumIntegerDigits:2,useGrouping: false,})+d+e.getSeconds().toLocaleString("fr-FR",{minimumIntegerDigits:2,useGrouping: false });},1000);