const timer=document.querySelector("#com");let separateur=" ";let date=new Date();setInterval(()=>{date=new Date();separateur=date.getSeconds() % 2== 0? " ":'<span class="flash" style="color:var(--txt-accent-color)">:</span>';timer.innerHTML=date.getHours().toLocaleString("fr-FR",{minimumIntegerDigits:2,useGrouping:false,})+separateur+date.getMinutes().toLocaleString("fr-FR",{minimumIntegerDigits:2,useGrouping:false,})+separateur+date.getSeconds().toLocaleString("fr-FR",{minimumIntegerDigits:2,useGrouping: false });},1000);